import styled from "styled-components";

const LoginStyle = styled.div`
position: absolute;
top: 20%;
left: 50%;
transform: translateX(-50%);
h2{
	
}
/*text-align: center;*/
font-size:20px;
label{
	font-size:14px;
	text-align: left;
}
input{
	display: block;
	padding: 5px 10px;
	font-size: 16px;
	margin: 10px 0px;
}

button{
	display:block;
	margin: 0px auto;
	font-size: 16px;
	width:100px;
	height: 40px;
}

`
export default LoginStyle
