import styled from "styled-components";

const LoginStyle = styled.div`
margin-top: 4000px;
display:flex;
justify-content:center;
text-align: center;
font-size:20px;
input{
	display: block;
	padding: 5px 15px;
	font-size: 16px;
	margin: 10px 0px;
}

button{
	display:block;
	margin: 0px auto;
	font-size: 16px;
	width:100px;
	height: 40px;
}

`
export default LoginStyle
