import styled from "styled-components";

const LoginStyle = styled.div`
display:flex;
justify-content:center;
text-align: center;
font-size:20px
input{
	display: block;
	padding: 5px 15px;
	font-size: 16px;
	margin: 10px 0 ;
}

button{
	display:block;
	margin: 0px auto;
}

`
export default LoginStyle
