import styled from "styled-components";

const RegistrStyle = styled.div`
position: absolute;
top: 20%;
left: 50%;
transform: translateX(-50%);
h2{
	text-align: center;
	font-size:30px;
}
label{
	font-size:14px;
	text-align: left;
}
input{
	display: block;
	padding: 5px 10px;
	font-size: 16px;
	margin: 5px 0 10px 0px;
	border-radius: 15px;
}

button{
	display:block;
	margin: 0px auto;
	font-size: 16px;
	width:200px;
	height: 40px;
}
`
export default RegistrStyle
