import styled from "styled-components";

const LoginStyle = styled.div`
position: absolute;
m20px
top: 20%;
left: 50%;
transform: translateX(-50%);
text-align: center;
font-size:20px;
input{
	display: block;
	padding: 5px 15px;
	font-size: 16px;
	margin: 10px 0px;
}

button{
	display:block;
	margin: 0px auto;
	font-size: 16px;
	width:100px;
	height: 40px;
}

`
export default LoginStyle
